sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// rightnow stores the current time
  var endoftheday = new GlideDateTime();

	//current day
	var today = endoftheday.getDate();


  // Create a GlideDateTime object for the When needed date
  var whenNeeded = new GlideDateTime(current.u_when_needed);

		//current day
	var istoday = whenNeeded.getDate();


  // If the When needed date is before rightnow, do not write the record to the database
  // Output an error message to the screen
  if(today.compareTo(istoday) == 0){
    gs.addErrorMessage("Your request has not been saved to the database. You should wait till the next day.");
	gs.info("Your request has not been saved to the database. You should wait till the next day. So please use the DateTime after " + endoftheday);dfsdf
	gs.warn("WARNING! Your request has not been saved to the database. You should wait till the next day. So please use the DateTime after " + endoftheday);
	gs.error("ERROR! Your request has not been saved to the database. You should wait till the next day. So please use the DateTime after " + endoftheday);
	gs.debug("Just DEBUGGING. Your request has not been saved to the database. You should wait till the next day. So please use the DateTime after " + endoftheday);
    current.setAbortAction(true);
  }

})(current, previous);
